mapper.update:
update board set
readhit = readhit + 1
where idx =#{idx}

update board set
subject = #{subject},content = #{content},pwd=#{pwd} where
idx = #{idx}

mapper.select:
select *
from board_show
where no between #{start} and #{end}

select nvl(count(*),0)
from board
where ref = #{ref} and step>#{step}
